// server/index.ts
import express3 from "express";

// server/routes.ts
import { createServer } from "http";
import express from "express";
import path from "path";
import fs from "fs";
async function registerRoutes(app2) {
  app2.use("/attached_assets", (req, res, next) => {
    res.header("Access-Control-Allow-Origin", "*");
    res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
    next();
  });
  app2.use("/attached_assets", express.static(path.join(process.cwd(), "attached_assets")));
  app2.get("/api/download-resume", (req, res) => {
    try {
      const resumePath = path.join(process.cwd(), "attached_assets", "Rajkumar_Devops_Resume_1750385906256.pdf");
      if (fs.existsSync(resumePath)) {
        res.download(resumePath, "Raj_Kumar_DevOps_Resume.pdf", (err) => {
          if (err) {
            console.error("Error downloading resume:", err);
            res.status(500).json({ message: "Error downloading resume" });
          }
        });
      } else {
        res.status(404).json({ message: "Resume file not found" });
      }
    } catch (error) {
      console.error("Resume download error:", error);
      res.status(500).json({ message: "Internal server error" });
    }
  });
  app2.post("/api/contact", (req, res) => {
    try {
      const { name, email, subject, message } = req.body;
      console.log("Contact form submission:", { name, email, subject, message });
      res.json({
        success: true,
        message: "Thank you for your message! I will get back to you soon."
      });
    } catch (error) {
      console.error("Contact form error:", error);
      res.status(500).json({
        success: false,
        message: "Error sending message. Please try again later."
      });
    }
  });
  const httpServer = createServer(app2);
  return httpServer;
}

